import React, { useContext } from 'react';
import { AuthContext } from '../auth/AuthContext';

export default function Profile() {
  const { user, logout } = useContext(AuthContext);
  if (!user) return <div>Loading...</div>;
  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({ first_name: user.first_name || '', last_name: user.last_name || '', email: user.email || '' });
  const [msg, setMsg] = useState(null);

  const save = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('/api/auth/me', { method: 'PUT', headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${localStorage.getItem('accessToken')}` }, body: JSON.stringify(form) });
      if (!res.ok) throw res;
      setMsg('Profile updated');
      setEditing(false);
    } catch (err) {
      const data = await (err.json ? err.json() : Promise.resolve({ message: 'Failed' }));
      setMsg(data.message || 'Failed to update');
    }
  };

  return (
    <div>
      <h2>Profile</h2>
      {msg && <div>{msg}</div>}
      {!editing ? (
        <div>
          <div>Name: {user.first_name} {user.last_name}</div>
          <div>Email: {user.email}</div>
          <div>Role: {user.role}</div>
          <div style={{ marginTop: 12 }}>
            {user.role === 'admin' && <a href="/admin" style={{ marginRight: 8 }}>Go to Admin Dashboard</a>}
            <a href="/change-password" style={{ marginRight: 8 }}>Change password</a>
            <button onClick={() => setEditing(true)}>Edit profile</button>
            <button onClick={logout} style={{ marginLeft: 8 }}>Logout</button>
          </div>
        </div>
      ) : (
        <form onSubmit={save} style={{ maxWidth: 600 }}>
          <input placeholder="First name" value={form.first_name} onChange={e=>setForm({...form, first_name:e.target.value})} />
          <input placeholder="Last name" value={form.last_name} onChange={e=>setForm({...form, last_name:e.target.value})} />
          <input placeholder="Email" value={form.email} onChange={e=>setForm({...form, email:e.target.value})} />
          <div style={{ marginTop: 8 }}>
            <button type="submit">Save</button>
            <button type="button" onClick={()=>setEditing(false)} style={{ marginLeft: 8 }}>Cancel</button>
          </div>
        </form>
      )}
    </div>
  );
}
